function chunkArrayInGroups(arr, size) {
  // Break it up.
  let out_arr = [];
  let number_of_groups = Math.ceil(arr.length / size );
  let group_index = 0;
  let count = 0;
  while (group_index < number_of_groups){
    out_arr[group_index] = [];
    for (let i = 0; i < size; i++) {
      if (arr[count] === undefined){
        break;
      } else {
        out_arr[group_index][i] = arr[count]; 
        count++;
    }
  }
    group_index++ 
  }
  return out_arr;
}

