function frankenSplice(arr1, arr2, n) {
  // It's alive. It's alive!
  let newarr = [...arr2];
  for (let i=0; i < arr1.length; i++){
    newarr.splice(n, 0, arr1[i])
    n++
  }
  return newarr;
}

console.log(frankenSplice([1, 2, 3], [4, 5, 6], 1))
console.log(frankenSplice([1, 2, 3], [4, 5], 1))
