function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  arr.sort((x,y) => x - y);
  let index = 0;
  while (index < arr.length){
      if (num <= arr[index]){
        break;}
      index++ 
  } 
return index;
}

