function sumAll(arr) {
  const start = arr[0] < arr[1] ? arr[0] : arr[1];
  const span = Math.abs(arr[0] - arr[1]) + 1;
  const mapFx = x => x = x + start;
  const fullArr = [...Array(span).keys()].map(mapFx);
  const reducer = (acc, item) => acc + item; 
  return fullArr.reduce(reducer);
}

